mapper.select:
select id, content, readflag, empno, userid, intime, type from smart_com_kj_chat where comid = #{comid} and empno = #{empno} and userid = #{khuserid} and
type = #{type}
and readflag = '0' order by intime desc

SELECT msgid,mtitle,mdesc,mhttpurl,pushtime,status
FROM smart_com_wxmsg  WHERE comid=#{comid} AND tuserid=#{userid}

    and msgtype=#{btagcode}


    and btagcode=#{btagcode}


    and otype=#{otype}

order by pushtime desc

mapper.update:
update smart_com_wxmsg
set status=#{status} ,readtime=now()
where comid=#{comid}  and msgid=#{msgid}

mapper.select:
SELECT
    msgid AS id,
    mcontent AS content,
    DATE_FORMAT(pushtime, '%Y-%m-%d') AS day
FROM smart_com_wxmsg
WHERE comid = #{comid}
  AND fuserid = #{fuserid}
  AND tuserid = #{tuserid}
  AND msgtype = 'FOLLOW'
ORDER BY pushtime DESC

mapper.delete:
DELETE FROM smart_com_wxmsg
WHERE comid = #{comid}
  AND msgid = #{msgid}
  AND msgtype = 'FOLLOW'
  AND fuserid = #{fuserid}
